import{_ as h}from"./plugin-vue_export-helper-c27b6911.js";import{r as i,o as l,c as n,d as t,e as d,f as a,b as r}from"./app-1f9851bd.js";const s={},o=r('<h1 id="linux-day02" tabindex="-1"><a class="header-anchor" href="#linux-day02" aria-hidden="true">#</a> Linux-Day02</h1><h2 id="网络常识" tabindex="-1"><a class="header-anchor" href="#网络常识" aria-hidden="true">#</a> 网络常识</h2><p>桥接模式：使用你的真实网卡,相当于你的局域网中多了一台电脑</p><p>仅主机模式：通过虚拟网卡将消息转发给真机,真机再把消息发到外部局域网,虚拟机里面装的软件,外部机器访问不到</p><p>NAT模式：可以把虚拟机的端口映射到真机上,外部机器可以访问真机的ip和端口从而访问到虚拟机</p><h2 id="_1-软件安装" tabindex="-1"><a class="header-anchor" href="#_1-软件安装" aria-hidden="true">#</a> 1. 软件安装</h2>',6),c={href:"https://www.bilibili.com/video/BV13a411q753?p=132&vd_source=8a1412d04d200fa33278d2b150f9b8df",target:"_blank",rel:"noopener noreferrer"},_=r('<h3 id="_1-1-软件安装方式" tabindex="-1"><a class="header-anchor" href="#_1-1-软件安装方式" aria-hidden="true">#</a> 1.1 软件安装方式</h3><p>在Linux系统中，安装软件的方式主要有四种，这四种安装方式的特点如下</p><table><thead><tr><th>安装方式</th><th>特点</th></tr></thead><tbody><tr><td>二进制发布</td><td>软件已经针对具体平台编译打包发布，只要解压，修改配置即可</td></tr><tr><td>rpm安装</td><td>软件已经按照redhat的包管理规范进行打包，使用rpm命令进行安装，<mark>不能自行解决库依赖问题</mark></td></tr><tr><td>yum安装</td><td>一种在线软件安装方式，本质上还是rpm安装，自动下载安装包并安装，安装过程中自动解决库依赖问题(安装过程需要联网)</td></tr><tr><td>源码编译安装</td><td>软件以源码工程的形式发布，需要自己编译打包</td></tr></tbody></table><h3 id="_1-2-安装jdk" tabindex="-1"><a class="header-anchor" href="#_1-2-安装jdk" aria-hidden="true">#</a> 1.2 安装JDK</h3><h3 id="_1-3-安装tomcat" tabindex="-1"><a class="header-anchor" href="#_1-3-安装tomcat" aria-hidden="true">#</a> 1.3 安装Tomcat</h3><h4 id="防火墙操作" tabindex="-1"><a class="header-anchor" href="#防火墙操作" aria-hidden="true">#</a> 防火墙操作</h4><table><thead><tr><th>操作</th><th>指令</th><th>备注</th></tr></thead><tbody><tr><td>暂时关闭防火墙</td><td>systemctl stop firewalld</td><td></td></tr><tr><td>永久关闭防火墙(禁用开机自启)</td><td>systemctl disable firewalld</td><td><mark>下次启动,才生效</mark></td></tr><tr><td>暂时开启防火墙</td><td>systemctl start firewalld</td><td></td></tr><tr><td>永久开启防火墙(启用开机自启)</td><td>systemctl enable firewalld</td><td><mark>下次启动,才生效</mark></td></tr><tr><td>查看防火墙状态</td><td>systemctl status firewalld / firewall-cmd --state</td><td></td></tr><tr><td>开放指定端口</td><td>firewall-cmd --zone=public --add-port=8080/tcp --permanent</td><td><mark>需要重新加载生效</mark></td></tr><tr><td>关闭指定端口</td><td>firewall-cmd --zone=public --remove-port=8080/tcp --permanent</td><td><mark>需要重新加载生效</mark></td></tr><tr><td>立即生效(重新加载)</td><td>firewall-cmd --reload</td><td></td></tr><tr><td>查看开放端口</td><td>firewall-cmd --zone=public --list-ports</td><td></td></tr></tbody></table><blockquote><p>注意：</p><p>​A. systemctl是管理Linux中服务的命令，可以对服务进行启动、停止、重启、查看状态等操作</p><p>​B. firewall-cmd是Linux中专门用于控制防火墙的命令</p><p>​C. 为了保证系统安全，服务器的防火墙不建议关闭</p></blockquote><h3 id="_1-4-安装mysql" tabindex="-1"><a class="header-anchor" href="#_1-4-安装mysql" aria-hidden="true">#</a> 1.4 安装MySQL</h3><h3 id="_1-5-安装lrzsz" tabindex="-1"><a class="header-anchor" href="#_1-5-安装lrzsz" aria-hidden="true">#</a> 1.5 安装lrzsz</h3><h2 id="_2-项目部署" tabindex="-1"><a class="header-anchor" href="#_2-项目部署" aria-hidden="true">#</a> 2. 项目部署</h2>',11),p={href:"https://www.bilibili.com/video/BV13a411q753?p=139&vd_source=8a1412d04d200fa33278d2b150f9b8df",target:"_blank",rel:"noopener noreferrer"},m=t("p",null,"演示项目部署，分别是：手动部署项目 和 基于shell脚本自动部署",-1),f=t("h3",{id:"_2-1-手动部署项目",tabindex:"-1"},[t("a",{class:"header-anchor",href:"#_2-1-手动部署项目","aria-hidden":"true"},"#"),d(" 2.1 手动部署项目")],-1),b=t("h3",{id:"_2-2-基于shell脚本自动部署",tabindex:"-1"},[t("a",{class:"header-anchor",href:"#_2-2-基于shell脚本自动部署","aria-hidden":"true"},"#"),d(" 2.2 基于Shell脚本自动部署")],-1);function u(x,k){const e=i("ExternalLinkIcon");return l(),n("div",null,[o,t("p",null,[t("a",c,[d("软件安装视频地址"),a(e)])]),_,t("p",null,[t("a",p,[d("项目部署视频地址"),a(e)])]),m,f,b])}const z=h(s,[["render",u],["__file","day02.html.vue"]]);export{z as default};
