import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{o as e,c as i,a,e as t,f as d,b as r}from"./app-3245b0be.js";const n={},h=t("h1",{id:"html标签",tabindex:"-1"},[t("a",{class:"header-anchor",href:"#html标签","aria-hidden":"true"},"#"),d(" HTML标签")],-1),s=r('<h2 id="布局标签" tabindex="-1"><a class="header-anchor" href="#布局标签" aria-hidden="true">#</a> 布局标签</h2><p>实际开发网页中，会大量频繁的使用 div 和 span 这两个没有语义的布局标签。</p><div class="hint-container info"><p class="hint-container-title">标签：&lt;div&gt; &lt;span&gt;</p><p>div标签：</p><ul><li><p>一行只显示一个（独占一行）</p></li><li><p>宽度默认是父元素的宽度，高度默认由内容撑开</p></li><li><p>可以设置宽高（width、height）</p></li></ul><p>span标签：</p><ul><li><p>一行可以显示多个</p></li><li><p>宽度和高度默认由内容撑开</p></li><li><p>不可以设置宽高（width、height）</p></li></ul></div><h2 id="段落标签" tabindex="-1"><a class="header-anchor" href="#段落标签" aria-hidden="true">#</a> 段落标签</h2><div class="hint-container info"><p class="hint-container-title">标签 &lt;br&gt; &lt;p&gt;</p><ul><li>&lt;p&gt; 这是一个段落标签 &lt;/p&gt;</li><li>&lt;br&gt; 这是一个换行标签 &lt;/br&gt;</li></ul></div><h2 id="超链接" tabindex="-1"><a class="header-anchor" href="#超链接" aria-hidden="true">#</a> 超链接</h2><div class="hint-container info"><p class="hint-container-title">标签: &lt;a href=&quot;...&quot; target=&quot;...&quot;&gt;央视网&lt;/a&gt;</p><ul><li>href: 指定资源访问的<strong>url</strong></li><li>target: 指定在何处打开资源链接 <ul><li>_self: 默认值，在当前页面打开</li><li>_blank: 在空白页面打开</li></ul></li></ul></div><h2 id="视频、音频标签" tabindex="-1"><a class="header-anchor" href="#视频、音频标签" aria-hidden="true">#</a> 视频、音频标签</h2><div class="hint-container info"><p class="hint-container-title">&lt;video&gt; &lt;audio&gt;</p><p>&lt;video&gt;</p><ul><li>src: 规定视频的url</li><li>controls: 显示播放控件</li><li>width: 播放器的宽度</li><li>height: 播放器的高度</li></ul><p>&lt;audio&gt;</p><ul><li>src: 规定音频的url</li><li>controls: 显示播放控件</li></ul></div><h2 id="文本格式标签" tabindex="-1"><a class="header-anchor" href="#文本格式标签" aria-hidden="true">#</a> 文本格式标签</h2><table><thead><tr><th>效果</th><th>标签</th><th>标签(强调)</th></tr></thead><tbody><tr><td>加粗</td><td>b</td><td>strong</td></tr><tr><td>倾斜</td><td>i</td><td>em</td></tr><tr><td>下划线</td><td>u</td><td>ins</td></tr><tr><td>删除线</td><td>s</td><td>del</td></tr></tbody></table><h2 id="注意事项" tabindex="-1"><a class="header-anchor" href="#注意事项" aria-hidden="true">#</a> 注意事项</h2><ul><li><p>在HTML页面中无论输入了多少个空格, 最多只会显示一个。<br> 可以使用空格占位符（ &amp;nbsp; ）来生成空格</p></li><li><p>在HTML中，除了空格占位符以外，还有一些其他的占位符如下：</p><table><thead><tr><th style="text-align:left;">显示结果</th><th style="text-align:left;">描述</th><th style="text-align:left;">占位符</th></tr></thead><tbody><tr><td style="text-align:left;"></td><td style="text-align:left;">空格</td><td style="text-align:left;">&amp;nbsp;</td></tr><tr><td style="text-align:left;">&lt;</td><td style="text-align:left;">小于号</td><td style="text-align:left;">&amp;lt;</td></tr><tr><td style="text-align:left;">&gt;</td><td style="text-align:left;">大于号</td><td style="text-align:left;">&amp;gt;</td></tr><tr><td style="text-align:left;">&amp;</td><td style="text-align:left;">和号</td><td style="text-align:left;">&amp;amp;</td></tr><tr><td style="text-align:left;">&quot;</td><td style="text-align:left;">引号</td><td style="text-align:left;">&amp;quot;</td></tr><tr><td style="text-align:left;">&#39;</td><td style="text-align:left;">撇号</td><td style="text-align:left;">&amp;apos;</td></tr></tbody></table></li></ul>',13);function o(p,c){return e(),i("div",null,[h,a(" more "),s])}const u=l(n,[["render",o],["__file","html02.html.vue"]]);export{u as default};
