import{_ as r}from"./plugin-vue_export-helper-c27b6911.js";import{o as n,c as i,a,d as t,e as s,b as o}from"./app-7601f1f9.js";const e="/assets/image-20221205001346266-aeaeee38.png",g="/assets/1667546920773-399b2129.png",d="/assets/1667547421140-7270fe70.png",p="/assets/1667547561387-acaf49c0.png",l="/assets/1667547604517-e102f4d3.png",c="/assets/1667548530745-caedbf81.png",f="/assets/1667548590602-2076c085.png",h="/assets/1667548969631-4648ff4f.png",b={},_=t("h1",{id:"javaweb",tabindex:"-1"},[t("a",{class:"header-anchor",href:"#javaweb","aria-hidden":"true"},"#"),s(" JavaWeb")],-1),m=o('<h2 id="什么是-web-开发" tabindex="-1"><a class="header-anchor" href="#什么是-web-开发" aria-hidden="true">#</a> 什么是 web 开发</h2><p><strong>Web</strong>：全球广域网，也称为<strong>万维网</strong> ( www <strong>W</strong>orld <strong>W</strong>ide <strong>W</strong>eb ) ，能够通过浏览器访问的<strong>网站</strong>。</p><figure><img src="'+e+'" alt=" " tabindex="0" loading="lazy"><figcaption></figcaption></figure><h2 id="网站的工作流程" tabindex="-1"><a class="header-anchor" href="#网站的工作流程" aria-hidden="true">#</a> 网站的工作流程</h2><ol><li><p>首先我们需要通过<strong>浏览器</strong>访问发布到<strong>前端服务器</strong>中的<strong>前端程序</strong>，这时候前端程序会将前端代码返回给浏览器</p><figure><img src="'+g+'" alt=" " tabindex="0" loading="lazy"><figcaption></figcaption></figure></li><li><p>浏览器得到前端代码，此时浏览器会将前端代码进行解析，展示到浏览器的窗口中，这时候我们就看到了<strong>网站</strong>的<strong>页面</strong></p><figure><img src="'+d+'" alt=" " tabindex="0" loading="lazy"><figcaption></figcaption></figure></li><li><p>但是此时页面是没有数据的，因为数据在数据库中，所以浏览器需要根据<strong>前端代码中指定的后台服务器的地址</strong>向<strong>后台服务器</strong>（内部有java程序）发起<strong>请求</strong>，后台服务器再去从<strong>数据库</strong>中获取数据，随后返回给浏览器。</p><figure><img src="'+p+'" alt=" " tabindex="0" loading="lazy"><figcaption></figcaption></figure></li><li><p>浏览器拿到后台返回的数据后，将数据展示在前端资源也就是<strong>网页</strong>上</p><figure><img src="'+l+'" alt=" " tabindex="0" loading="lazy"><figcaption></figcaption></figure></li></ol><p><strong>整个流程如下：</strong></p><ol><li><p>浏览器先向前端服务器请求<strong>前端资源</strong>，也就是我们所说的<strong>网页</strong></p></li><li><p>浏览器再向<strong>后台服务器</strong>发起请求，获取<strong>数据</strong></p></li><li><p>浏览器将得到的后台<strong>数据</strong>填充到<strong>网页</strong>上，展示给用户去看</p></li></ol><h2 id="网站的开发模式" tabindex="-1"><a class="header-anchor" href="#网站的开发模式" aria-hidden="true">#</a> 网站的开发模式</h2><p>主要有2种：前后端分离和混合开发</p><p><strong>前后端分离</strong>： <strong>目前企业开发的主流</strong></p><div class="hint-container note"><p class="hint-container-title">特点</p><ul><li><p>前端人员开发前端程序，前端程序单独部署到前端服务器上</p></li><li><p>后端人员开发后端程序，后端程序单独部署到后端服务器上</p></li></ul></div><figure><img src="'+c+'" alt=" " tabindex="0" loading="lazy"><figcaption></figcaption></figure><p><strong>混合开发</strong>：<strong>早期开发技术</strong></p><div class="hint-container note"><p class="hint-container-title">特点</p><p>前端人员开发的代码和后端人员开发的代码在同一个项目中，一起打包部署。</p></div><figure><img src="'+f+'" alt=" " tabindex="0" loading="lazy"><figcaption></figcaption></figure><h2 id="网站的开发技术" tabindex="-1"><a class="header-anchor" href="#网站的开发技术" aria-hidden="true">#</a> 网站的开发技术</h2><figure><img src="'+h+'" alt=" " tabindex="0" loading="lazy"><figcaption></figcaption></figure><p><strong>前端web开发</strong>：</p><table><thead><tr><th>技术</th><th>描述</th></tr></thead><tbody><tr><td>HTML</td><td>用于构建网站的基础结构</td></tr><tr><td>CSS</td><td>用于美化页面</td></tr><tr><td>JavaScript</td><td>实现网页和用户的交互</td></tr><tr><td>Vue</td><td>将数据填充到html页面上的</td></tr><tr><td>Element</td><td>提供一些美观的组件</td></tr><tr><td>Nginx</td><td>一款web服务器软件，用于部署前端工程</td></tr></tbody></table><p><strong>后端web开发</strong>：</p><table><thead><tr><th>技术</th><th>描述</th></tr></thead><tbody><tr><td>Maven</td><td>管理项目的软件</td></tr><tr><td>Mysql</td><td>数据库软件之一</td></tr><tr><td>SpringBoot</td><td>spring家族的产品，当前最为主流的项目开发技术。</td></tr><tr><td>Mybatis</td><td>操作数据库的框架</td></tr></tbody></table>',21);function u(x,y){return n(),i("div",null,[_,a(" more "),m])}const z=r(b,[["render",u],["__file","index.html.vue"]]);export{z as default};
