    <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>include</span> <span class="token attr-name">refid</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>commonSelect<span class="token punctuation">&quot;</span></span><span class="token punctuation">/&gt;</span></span>
